mapper.select:
SELECT charges_id, ticket_id, charges_rental, charges_time, charges_state, isSendmail, charges_number_Omise, zxingCodePath, sign, isUsed, cardNumber, name, country, company, email, telephone, note FROM edcon_charges WHERE charges_state=0 AND isdelete=0
AND match(name,country,company,position,email,telephone) against (#{select} IN BOOLEAN MODE )
LIMIT #{start},#{ps};

mapper.update:
UPDATE
edcon_charges
SET country = #{country}, company = #{company}, position = #{position}, email = #{email}, telephone
= #{telephone}
WHERE charges_id = #{chargesId};
